import React from "react";
import MyPhoto from '../MyPhoto/MyPhoto';
import Button from '../Button/Button';
import Logo from './hemant garg.jpg';
import './Projects.css';

class Projects extends React.Component {
	showModal(){
		var modal = document.getElementById('myModal');
		var span = document.getElementsByClassName("close")[0];

		modal.style.display = "block";

		span.onclick = function() {
			modal.style.display = "none";
		}
		window.onclick = function(event) {
			if (event.target === modal) {
				modal.style.display = "none";
			}
		}
	}
	render(){
		return(
			<main className="Projects" style={{paddingTop: 12+'rem'}}>
				<div id="myModal" class="modal">
					<div class="modal-content">
					<span class="close">&times;</span>
					<h1 className="modal-content-title">MAILZY</h1>
					<p className="modal-content-description">	
						The app that can be used to send mass emails to a big 
						list of users for the purpose of collecting feedback.
						<br />
						If you are running a startup or any kind of business and you 
						want to collect some feedback from your users to expand or 
						improve your business then you can use this web app.
						<br />
					</p>
					<p className="modal-content-tech">
						 Skills that I've used: <br/>
						<span>Reactjs</span>
					<span>Redux</span><span>React-router</span><span>Redux-form</span>
					<span>Sass</span><span>Expressjs</span><span>MongoDB</span><span>passport</span>
					<span>Stripe</span><span>Sendgrid</span><span>Nodemailer</span><span>Google OAuth</span> 
					</p>
					<Button onclick={''} color="#6D214F" text="Repo" iconClass = "fas fa-briefcase" />
					<Button onclick={''} color="#2980b9" text="Live" iconClass = "fas fa-briefcase" />
					</div>
				</div>
				<h1 className="my-name">Recent Projects</h1>
				<div className="project-list">
					<MyPhoto text onclick={this.showModal} logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
				</div>
			</main>
		)
	}
};

export default Projects;
