import React from "react";
import MyPhoto from '../MyPhoto/MyPhoto';
import Logo from './hemant garg.jpg';
import './Projects.css';

class Projects extends React.Component {
	showModal(){
		var modal = document.getElementById('myModal');
		var btn = document.getElementById("myBtn");
		var span = document.getElementsByClassName("close")[0];
		btn.onclick = function() {
		modal.style.display = "block";
		}
		span.onclick = function() {
		modal.style.display = "none";
		}
		window.onclick = function(event) {
		if (event.target == modal) {
			modal.style.display = "none";
		}
		}
	}
	render(){
		return(
			<main className="Projects" style={{paddingTop: 12+'rem'}}>
				<div id="myModal" class="modal">
					<div class="modal-content">
					<span class="close">&times;</span>
					<p>Some text in the Modal..</p>
					</div>
				</div>
				<h1 className="my-name">Recent Projects</h1>
				<div className="project-list">
					<MyPhoto text onclick={this.showModal} logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
					<MyPhoto logo={Logo} width={"20rem"}/>
				</div>
			</main>
		)
	}
};

export default Projects;
